import React, { useEffect, useState } from "react";
import axios from "axios"; // Optional if you're using axios to fetch data

const Customers = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1); // Keep track of the current page
  const [pageSize, setPageSize] = useState(5); // Define the number of items per page
  const [sortConfig, setSortConfig] = useState({ key: "id", direction: "asc" }); // Sorting config
  const [newCustomer, setNewCustomer] = useState({
    name: "",
    place: "",
    phoneNumber: "",
    emailID: "",
    baseAmount: "",
  });

  // Fetch customer data from the API when the component mounts
  useEffect(() => {
    axios
      .get("https://localhost:7053/api/customers") // Replace with your actual API URL
      .then((response) => {
        setCustomers(response.data); // Update state with customer data
        setLoading(false); // Stop loading once data is fetched
      })
      .catch((error) => {
        console.error("There was an error fetching the customer data!", error);
        setLoading(false); // Stop loading even if there is an error
      });
  }, []);

  // Handle sorting
  const handleSort = (key) => {
    let direction = "asc";
    if (sortConfig.key === key && sortConfig.direction === "asc") {
      direction = "desc";
    }
    setSortConfig({ key, direction });
  };

  // Sort customers based on the current sorting configuration
  const sortedCustomers = [...customers].sort((a, b) => {
    if (a[sortConfig.key] < b[sortConfig.key]) {
      return sortConfig.direction === "asc" ? -1 : 1;
    }
    if (a[sortConfig.key] > b[sortConfig.key]) {
      return sortConfig.direction === "asc" ? 1 : -1;
    }
    return 0;
  });

  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage > 0 && newPage <= Math.ceil(customers.length / pageSize)) {
      setCurrentPage(newPage);
    }
  };

  // Handle inserting a new customer
  const handleInsert = () => {
    axios
      .post("https://localhost:7053/api/customers", newCustomer) // Replace with your actual API URL
      .then((response) => {
        setCustomers([...customers, response.data]); // Add new customer to the list
        setNewCustomer({
          name: "",
          place: "",
          phoneNumber: "",
          emailID: "",
          baseAmount: "",
        }); // Clear the form fields
      })
      .catch((error) => {
        console.error("There was an error inserting the customer data!", error);
      });
  };

  // Handle updating an existing customer
  const handleUpdate = (id) => {
    const updatedCustomer = customers.find((customer) => customer.id === id);
    if (!updatedCustomer) return;
    // Replace with the logic to update your customer information
    axios
      .put(`https://localhost:7053/api/customers/${id}`, updatedCustomer) // Replace with your actual API URL
      .then((response) => {
        setCustomers(customers.map((customer) => (customer.id === id ? response.data : customer))); // Update customer in the list
      })
      .catch((error) => {
        console.error("There was an error updating the customer data!", error);
      });
  };

  // Handle deleting a customer
  const handleDelete = (id) => {
    axios
      .delete(`https://localhost:7053/api/customers/${id}`) // Replace with your actual API URL
      .then(() => {
        setCustomers(customers.filter((customer) => customer.id !== id)); // Remove the customer from the list
      })
      .catch((error) => {
        console.error("There was an error deleting the customer data!", error);
      });
  };

  // Calculate the items for the current page
  const indexOfLastCustomer = currentPage * pageSize;
  const indexOfFirstCustomer = indexOfLastCustomer - pageSize;
  const currentCustomers = sortedCustomers.slice(indexOfFirstCustomer, indexOfLastCustomer);

  if (loading) return <div>Loading...</div>; // Show a loading message while data is being fetched

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-3">Customer List</h2>
      <table className="table table-bordered striped bordered hover responsive">
        <thead className="table-dark">
          {/* <tr>
            <th onClick={() => handleSort("id")}>
              ID {sortConfig.key === "id" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("name")}>
              Name {sortConfig.key === "name" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("place")}>
              Place {sortConfig.key === "place" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("phoneNumber")}>
              Phone Number {sortConfig.key === "phoneNumber" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("emailID")}>
              Email ID {sortConfig.key === "emailID" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("baseAmount")}>
              Base Amount {sortConfig.key === "baseAmount" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th>Actions</th>
          </tr> */}
          <tr>
    <th onClick={() => handleSort("id")}>ID</th>
    <th onClick={() => handleSort("name")}>Name</th>
    <th onClick={() => handleSort("place")}>Place</th>
    <th onClick={() => handleSort("phoneNumber")}>Phone Number</th>
    <th onClick={() => handleSort("emailID")}>Email ID</th>
    <th onClick={() => handleSort("baseAmount")}>Base Amount</th>
    <th>Actions</th> {/* Add actions for Update and Delete */}
  </tr>
        </thead>
        <tbody>
          {/* Map through the current page's customers */}
          {currentCustomers.length > 0 ? (
            currentCustomers.map((customer, index) => (
              <tr key={customer.id || index}>
                <td>{index + 1}</td>
                <td>{customer.name}</td>
                <td>{customer.place}</td>
                <td>{customer.phoneNumber}</td>
                <td>{customer.emailID}</td>
                <td>{customer.baseAmount}</td>
                <td>
                  <button onClick={() => handleUpdate(customer.id)} className="btn btn-primary btn-sm">Update</button>
                  <button onClick={() => handleDelete(customer.id)} className="btn btn-danger btn-sm ml-2">Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7" className="text-center">
                No data available
              </td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Paging controls */}
      <div className="pagination-controls text-center">
        <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
          Previous
        </button>
        <span>
          Page {currentPage} of {Math.ceil(customers.length / pageSize)}
        </span>
        <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === Math.ceil(customers.length / pageSize)}>
          Next
        </button>
      </div>

      {/* Form for adding a new customer */}
      <div className="mt-4">
        <h3>Add New Customer</h3>
        <input
          type="text"
          placeholder="Name"
          value={newCustomer.name}
          onChange={(e) => setNewCustomer({ ...newCustomer, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Place"
          value={newCustomer.place}
          onChange={(e) => setNewCustomer({ ...newCustomer, place: e.target.value })}
        />
        <input
          type="text"
          placeholder="Phone Number"
          value={newCustomer.phoneNumber}
          onChange={(e) => setNewCustomer({ ...newCustomer, phoneNumber: e.target.value })}
        />
        <input
          type="email"
          placeholder="Email ID"
          value={newCustomer.emailID}
          onChange={(e) => setNewCustomer({ ...newCustomer, emailID: e.target.value })}
        />
        <input
          type="number"
          placeholder="Base Amount"
          value={newCustomer.baseAmount}
          onChange={(e) => setNewCustomer({ ...newCustomer, baseAmount: e.target.value })}
        />
        <button onClick={handleInsert} className="btn btn-success mt-2">
          Add Customer
        </button>
      </div>
    </div>
  );
};

export default Customers;
